         print ( "Cutting the pizza into diagonal slices" ) 

     def   box ( self ): 
         print ( "Place pizza in official PizzaStore box" ) 

     def   get_name ( self ): 
         return   self . _name  class   NYStyleCheesePizza ( Pizza ): 
     def   __init__ ( self ): 
         super ( NYStyleCheesePizza ,   self ) . __init__ () 
         self . _name   =   "NY Style Sauce and Cheese Pizza"  class   NYStylePepperoniPizza ( Pizza ): 
     def   __init__ ( self ): 
         super ( NYStylePepperoniPizza ,   self ) . __init__ () 
         self . _name   =   "NY Style Pepperoni Pizza"  class   NYStyleClamPizza ( Pizza ): 
