     def   __init__ ( self ): 
         super ( ChicagoStyleCheesePizza ,   self ) . __init__ () 
         self . _name   =   "Chicago Style Deep Dish Cheese Pizza" 

     def   cut ( self ): 
         print ( "Cutting the pizza into square slices" )  class   ChicagoStylePepperoniPizza ( Pizza ): 
     def   __init__ ( self ): 
         super ( ChicagoStylePepperoniPizza ,   self ) . __init__ () 
         self . _name   =   "Chicago Style Pepperoni Pizza" 

     def   cut ( self ): 
         print ( "Cutting the pizza into square slices" )  class   ChicagoStyleClamPizza ( Pizza ): 
     def   __init__ ( self ): 
         super ( ChicagoStyleClamPizza ,   self ) . __init__ () 
         self . _name   =   "Chicago Style Clam Pizza" 

     def   cut ( self ): 
         print ( "Cutting the pizza into square slices" )  class   ChicagoStyleVeggiePizza ( Pizza ): 
     def   __init__ ( self ): 
         super ( ChicagoStyleVeggiePizza ,   self ) . __init__ () 
         self . _name   =   "Chicago Deep Dish Veggie Pizza" 

     def   cut ( self ): 
         print ( "Cutting the pizza into square slices" )  ########factory store code#######  class   PizzaStore : 
     __metaclass__   =   ABCMeta 

     @abstractmethod 
     def   create_pizza ( self ,   type ): 
         pass 

     def   order_pizza ( self ,   pizza_type ): 
         pizza   =   self . create_pizza ( pizza_type ) 
         print ( "--- Making a "   +   pizza . get_name ()   +   " ---" ) 

         pizza . prepare () 
         pizza . bake () 
         pizza . cut () 
         pizza . box () 

         return   pizza  class   NYPizzaStore ( PizzaStore ): 
     def   create_pizza ( self ,   type ): 
         style_pizza_dict   =   dict ( cheese = NYStyleCheesePizza ,   veggie = NYStyleVeggiePizza ,   clam = NYStyleClamPizza , 
                                 pepperoni = NYStylePepperoniPizza ) 
         return   style_pizza_dict [ type ]()  class   ChicagoPizzaStore ( PizzaStore ): 
     def   create_pizza ( self ,   type ): 
         style_pizza_dict   =   dict ( cheese = ChicagoStyleCheesePizza ,   veggie = ChicagoStyleVeggiePizza , 
                                 clam = ChicagoStyleClamPizza , 
                                 pepperoni = ChicagoStylePepperoniPizza ) 
         return   style_pizza_dict [ type ]()  ######test code######  if   __name__   ==   '__main__' : 

     ny_store   =   NYPizzaStore () 
     chicago_store   =   ChicagoPizzaStore () 

     pizza   =   ny_store . order_pizza ( 'cheese' ) 
     print ( 'jack ordered a '   +   pizza . get_name ()   +   ' pizza' ) 

     pizza   =   chicago_store . order_pizza ( 'veggie' ) 
     print ( 'lucy ordered a '   +   pizza . get_name ()   +   ' pizza' ) 

     pizza   =   ny_store . order_pizza ( 'clam' ) 
     print ( 'lily ordered a '   +   pizza . get_name ()   +   ' pizza' )

抽象工厂模式提供一个接口，用于创建相关或依赖对象的家族，而不需要明确指定具体类。    抽象工厂的任务是创建一个负责创建一组产品的接口。这个接口内的每个方法都负责创建一个具体的产品。  示例代码： class   FreshClams : 
     def   __str__ ( self ): 
         return   'Fresh Clams'  class   MarinaraSauce : 
     def   __str__ ( self ): 
         return   "Marinara Sauce"  class   ThickCrustDough : 
     def   __str__ ( self ): 
         return   "Thick Crust Dough"  class   ReggianoCheese : 
     def   __str__ ( self ): 
         return   "Reggiano Cheese"  class   SlicedPepperoni : 
     def   __str__ ( self ): 
         return   "Sliced Pepperoni"  class   Garlic : 
     def   __str__ ( self ): 
         return   "Garlic"  class   Onion : 
     def   __str__ ( self ): 
         return   "Onion"  class   RedPepper : 
     def   __str__ ( self ): 
         return   "Red Pepper"  class   PizzaIngredientFactory : 
     '''      定义原料工厂      ''' 

     def   create_dough ( self ): 
         raise   NotImplementedError () 

     def   create_sauce ( self ): 
         raise   NotImplementedError () 

     def   create_cheese ( self ): 
         raise   NotImplementedError () 

     def   create_pepperoni ( self ): 
         raise   NotImplementedError () 

     def   create_clam ( self ): 
         raise   NotImplementedError () 

     def   create_veggies ( self ): 
         raise   NotImplementedError ()  class   NYPizzaIngredientFactory ( PizzaIngredientFactory ): 
     def   create_dough ( self ): 
         print ( "Tossing  %s "   %   ThickCrustDough ()) 
         return   ThickCrustDough () 

     def   create_sauce ( self ): 
         print ( "Adding  %s ..."   %   MarinaraSauce ()) 
         return   MarinaraSauce () 

     def   create_cheese ( self ): 
         print ( "Adding  %s ..."   %   ReggianoCheese ()) 
         return   ReggianoCheese () 

     def   create_pepperoni ( self ): 
         print ( "Adding  %s ..."   %   SlicedPepperoni ()) 
         return   SlicedPepperoni () 

     def   create_clam ( self ): 
         print ( "Adding  %s ..."   %   FreshClams ()) 
         return   FreshClams () 

     def   create_veggies ( self ): 
         veggies   =   [ Garlic (),   Onion (),   RedPepper ()] 
         for   veggie   in   veggies : 
             print ( "   %s "   %   veggie ) 
         return   veggies  class   Pizza : 
     '''      pizza 工厂      ''' 

     dough   =   None 
     sauce   =   None 
     cheese   =   None 
     veggies   =   [] 
     pepperoni   =   None 
     clam   =   None 

     def   __init__ ( self ,   name ,   ingredient_factory ): 
         self . name   =   name 
         self . ingredient_factory   =   ingredient_factory 

     def   prepare ( self ): 
         raise   NotImplementedError () 

     def   bake ( self ): 
         print ( "Bake for 25 minutes at 350" ) 

     def   cut ( self ): 
         print ( "Cutting the pizza into diagonal slices" ) 

     def   box ( self ): 
         print ( "Place pizza in official PizzaStore box" ) 

     def   __str__ ( self ): 
         return   self . name  class   NYStyleCheesePizza ( Pizza ): 
     def   prepare ( self ): 
         dough   =   self . ingredient_factory . create_dough () 
         sauce   =   self . ingredient_factory . create_sauce () 
         cheese   =   self . ingredient_factory . create_cheese () 
         clam   =   self . ingredient_factory . create_clam () 
         veggies   =   self . ingredient_factory . create_veggies ()  class   NYStyleClamPizza ( Pizza ): 
     def   prepare ( self ): 
         dough   =   self . ingredient_factory . create_dough () 
         sauce   =   self . ingredient_factory . create_sauce () 
         cheese   =   self . ingredient_factory . create_cheese () 
         clam   =   self . ingredient_factory . create_clam ()  class   PizzaStore : 
     '''      pizza 店工厂      ''' 

     ingredient_factory   =   None 

     def   create_pizza ( self ,   pizza_type ): 
         # 每个需要子类实现的方法都会抛出NotImplementedError 
         # 我们也可以把 PizzaStore 的 metaclass 设置成 abc.ABCMeta 
         # 这样的话，这个类就是真正的抽象基类 
         raise   NotImplementedError () 

     def   order_pizza ( self ,   pizza_type ):    # 现在把 pizza 的类型传入 order_pizza() 

         pizza   =   self . create_pizza ( pizza_type ) 

         #  一旦我们有了一个 pizza，需要做一些准备（擀面皮、加佐料），然后烘烤、切片、装盒 
         pizza . prepare () 
         pizza . bake () 
         pizza . cut () 
         pizza . box () 
         return   pizza 

         # 下边是其他可能用到的方法  class   NYStylePizzStore ( PizzaStore ): 
     ingredient_factory   =   NYPizzaIngredientFactory () 

     def   create_pizza ( self ,   pizza_type ): 
         # 根据 pizza 类型，我们实例化正确的具体类，然后将其赋值给 pizza 实例变量 
         if   pizza_type   ==   'cheese' : 
             pizza   =   NYStyleCheesePizza ( 'NY Style Sauce and Cheese Pizza' , 
                                        self . ingredient_factory ) 
         elif   pizza_type   ==   'clam' : 
             pizza   =   NYStyleClamPizza ( 'NY Style Clam Pizza' , 
                                      self . ingredient_factory ) 
         return   pizza  def   main (): 
     nystore   =   NYStylePizzStore () 
     pizza   =   nystore . order_pizza ( 'cheese' ) 
     print ( '*'   *   10 ) 
     print ( "goodspeed ordered a  %s "   %   pizza ) 
     print ( '*'   *   10 )  if   __name__   ==   '__main__' : 
     main ()

利用私有的构造器，只有单例类内部才可以调用构造器。  延迟方式的问题在于每次实例化都要调用此实例化方法，造成了无谓的性能消耗。  因为此模式很简单，我们直接上代码：  public   class   Singleton   { 
     private   static   Singleton   uniqueInstance ; 
     private   Singleton (){ 

     } 
     // 请求得到一个实例 : 延迟的方式 
     public   static   Singleton   GetInstance (){ 
         if ( uniqueInstance   ==   null ){ 
             uniqueInstance   =   new   Singleton (); 
         } 
         return   uniqueInstance ; 
     }  }  
这里如果使用多线程的话，上面的代码会有问题。所以一般还需要加上一个synchronized关键字： public   class   Singleton   { 
     private   static   Singleton   uniqueInstance ; 
     private   Singleton ()   {} 
     public   static   synchronized   Singleton   getInstance ()   { 
         if   ( uniqueInstance   ==   null )   { 
             uniqueInstance   =   new   Singleton (); 
         } 
         return   uniqueInstance ; 
     }

也很简单，直接上代码： public   class   ChocolateBoiler   { 
     private   boolean   empty ; 
     private   boolean   boiled ; 
     // 静态初始化器中创建单例。保证了线程安全 
     private   static   ChocolateBoiler   uniqueInstance   =   new   ChocolateBoiler (); 

     private   ChocolateBoiler (){ 
         this . empty   =   true ; 
         this . boiled   =   false ; 
     } 
     public   void   fill (){ 
         if ( isEmpty ()){ 
             empty   =   false ; 
             boiled   =   false ; 
         } 
     }  }  
还有一种针对Java中的特例，做的双重检查加锁： public   class   Singleton2   { 
     private   volatile   static   Singleton2   uniqueInstance ; 
     private   Singleton2 (){} 
     public   static   Singleton2   getInstance (){ 
         if ( uniqueInstance   ==   null ){ 
             synchronized   ( Singleton2 . class )   { 
                 if ( uniqueInstance   ==   null ){ 
                     uniqueInstance   =   new   Singleton2 (); 
                 } 
             } 
         } 
         return   uniqueInstance ; 
     }  }

